class Point{constructor(t,o){this.x=t,this.y=o}static x;static y}class Rect{static left=0;static right=0;static top=0;static bottom=0;constructor(t,o,i,r){this.top=o,this.left=t,this.bottom=o+r,this.right=t+i}center(){return new Point(right-left/2,top-bottom/2)}topLeft(){return new Point(left,top)}topRight(){return new Point(right,top)}bottomLeft(){return new Point(left,bottom)}bottomRight(){return new Point(right,bottom)}contains(t){return t.x>this.left&&t.x<this.right&&t.y>this.top&&t.y<this.bottom}}
